    // M√©todo para extraer el QueueSize del JSON de Jolokia
    private String extractQueueSize(String jsonResponse) {
        try {
            int startIndex = jsonResponse.indexOf("\"QueueSize\":");
            if (startIndex != -1) {
                startIndex += "\"QueueSize\":".length();
                int endIndex = jsonResponse.indexOf(",", startIndex);
                if (endIndex == -1) {
                    endIndex = jsonResponse.indexOf("}", startIndex);
                }
                if (endIndex != -1) {
                    return jsonResponse.substring(startIndex, endIndex).trim();
                }
            }
        } catch (Exception e) {
            System.err.println("Error extracting QueueSize: " + e.getMessage());
        }
        return "0";
    }
    
    // M√©todo para parsear el mensaje JSON real
    private List<String> parseRealMessage(String jsonMessage) {
        List<String> messages = new ArrayList<>();
        try {
            StringBuilder messageContent = new StringBuilder();
            messageContent.append("üìÑ MENSAJE REAL ENCONTRADO EN LA COLA:\n");
            messageContent.append("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n");
            
            // Extraer informaci√≥n del JSON
            if (jsonMessage.contains("\"messageId\"")) {
                messageContent.append("üìã messageId: ").append(extractJsonValue(jsonMessage, "messageId")).append("\n");
            }
            if (jsonMessage.contains("\"fileName\"")) {
                messageContent.append("üìÅ fileName: ").append(extractJsonValue(jsonMessage, "fileName")).append("\n");
            }
            if (jsonMessage.contains("\"status\"")) {
                messageContent.append("‚úÖ status: ").append(extractJsonValue(jsonMessage, "status")).append("\n");
            }
            if (jsonMessage.contains("\"receivedAt\"")) {
                messageContent.append("üìÖ receivedAt: ").append(extractJsonValue(jsonMessage, "receivedAt")).append("\n");
            }
            
            messageContent.append("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n");
            messageContent.append("üìä Contenido del Archivo Excel:\n");
            
            if (jsonMessage.contains("\"excelBase64\"")) {
                String base64Content = extractJsonValue(jsonMessage, "excelBase64");
                messageContent.append("üíæ Tama√±o del base64: ").append(base64Content.length()).append(" caracteres\n");
                messageContent.append("üîç Tipo: Excel Workbook (.xlsx)\n");
                messageContent.append("üìÑ Contenido base64 (primeros 100 caracteres):\n");
                messageContent.append("   ").append(base64Content.substring(0, Math.min(100, base64Content.length()))).append("...\n");
                messageContent.append("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n");
                messageContent.append("‚ÑπÔ∏è  Nota: Archivo Excel completo disponible en base64");
            } else {
                messageContent.append("‚ùå No se encontr√≥ contenido excelBase64 en el mensaje");
            }
            
            messages.add(messageContent.toString());
            
        } catch (Exception e) {
            messages.add("Error parseando mensaje JSON: " + e.getMessage());
            messages.add("Contenido crudo: " + jsonMessage);
        }
        return messages;
    }
    
    // M√©todo auxiliar para extraer valores del JSON
    private String extractJsonValue(String json, String key) {
        try {
            String pattern = "\"" + key + "\"\\s*:\\s*\"([^\"]+)\"";
            java.util.regex.Pattern p = java.util.regex.Pattern.compile(pattern);
            java.util.regex.Matcher m = p.matcher(json);
            if (m.find()) {
                return m.group(1);
            }
        } catch (Exception e) {
            System.err.println("Error extrayendo valor JSON: " + e.getMessage());
        }
        return "No encontrado";
    }
    
