    /**
     * Lee mensaje b√°sico de la cola usando solo ActiveMQ REST API
     * @return Contenido del mensaje o null si no se puede leer
     */
    public String readBasicMessageFromQueue() {
        try {
            System.out.println("=== LEYENDO MENSAJE B√ÅSICO DE LA COLA ===");
            
            // M√©todo corregido: usar GET para leer mensajes sin consumir (BROWSE)
            String browseUrl = "http://localhost:8161/api/message?destination=queue://" + queueName + "&type=queue";
            URL url = new URL(browseUrl);
            HttpURLConnection conn = (HttpURLConnection) url.openConnection();
            conn.setRequestMethod("GET");
            conn.setRequestProperty("Accept", "application/json");
            
            // Autenticaci√≥n b√°sica
            String auth = "admin:admin";
            String encodedAuth = java.util.Base64.getEncoder().encodeToString(auth.getBytes());
            conn.setRequestProperty("Authorization", "Basic " + encodedAuth);
            
            int responseCode = conn.getResponseCode();
            System.out.println("Basic Browse Response code: " + responseCode);
            
            if (responseCode == 200) {
                BufferedReader reader = new BufferedReader(new InputStreamReader(conn.getInputStream()));
                StringBuilder response = new StringBuilder();
                String line;
                while ((line = reader.readLine()) != null) {
                    response.append(line).append("\n");
                }
                reader.close();
                
                String messageBody = response.toString().trim();
                System.out.println("Mensaje b√°sico le√≠do: " + messageBody.length() + " caracteres");
                
                if (!messageBody.isEmpty()) {
                    return messageBody;
                }
            } else {
                System.out.println("No se pudo leer mensaje b√°sico, response code: " + responseCode);
            }
            
        } catch (Exception e) {
            System.err.println("Error reading basic message: " + e.getMessage());
            e.printStackTrace();
        }
        
        return null;
    }
    
